const initialState = {
    token: null,
    userId: null,
}


const AuthReducer = (state = initialState, action) => {
    switch (action.type) {
        case AUTH_LOGOUT:
            return {
                ...state,
                token: null,
                userId: null,
                authFailedMsg: null
            }
        default:
            return state;
    }
}

export default AuthReducer;
