const initState = {
    products: [],
    totalPrice: 0,
    totalQuantities: 0,

}

const CartReducer = (state = initState, action) => {
    switch (action.type) {
        case "ADD_TO_CART":
            const { product, quantity } = action.payload;
            const check = state.products.find(pr => pr.id === product.id)
            console.log(check);
            if (check) {
                return state;
            }
            else {
                const totalPrice = product.discountPrice * quantity;
                const totalQuantities = state.totalPrice + state.totalQuantities;
                
                return {
                    ...state,
                    products: [product, ...products],
                    totalPrice,
                    totalQuantities,
                }
            }
        default:
            return state;
    }

}

export default CartReducer;
