import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchOrder } from '../../store/productActionCreator';

const FetchOrder = () => {
    const dispatch = useDispatch();
    const { token, userId } = useSelector(state => state.AuthReducer);
    const { orders } = useSelector(state => state.ProductsReducer);

    useEffect(() => {
        dispatch(fetchOrder(token, userId));
    }, [])

    let rooms = [];
    if (this.props.bookedRoomErr) {
        rooms = <p style={{
            border: "1px solid grey",
            boxShadow: "1px 1px #888888",
            borderRadius: "5px",
            padding: "20px",
            marginBottom: "10px"
        }}>Sorry Failed to Load Booked Room Information</p>
    }
    else {
        if (this.props.bookedRoom.length === 0) {
            rooms = <p style={{
                border: "1px solid grey",
                boxShadow: "1px 1px #888888",
                borderRadius: "5px",
                padding: "20px",
                marginBottom: "10px"
            }}>You have not booked any room!</p>
        }
        else {
            rooms = this.props.bookedRoom.map(room => {
                return <BookedRoom room={room} key={room.id} />
            });
        }
    }


    return (
        <div className="mt-100">

        </div>
    )
}

export default FetchOrder;
