import React, { Component } from 'react'
import { Formik } from 'formik';
import { Alert } from 'reactstrap';

export default class Auth extends Component {
    render() {
        let form = null;
        form = <Formik
            initialValues={
                {
                    email: "",
                    password: "",
                    passwordConfirm: ""
                }
            }
            onSubmit={
                (values) => {
                    this.props.auth(values.email, values.password, this.state.mode)
                }
            }

            validate={(values) => {
                const errors = {};
                if (!values.email) {
                    errors.email = "Required";
                }
                else if (!/^([a-zA-Z0-9_\-\.]+)@([a-zA-Z0-9_\-\.]+)\.([a-zA-Z]{2,5})$/i.test(values.email)) {
                    errors.email = "Invalid Email Address";
                }
                if (!values.password) {
                    errors.password = "Required";
                }
                else if (values.password.length < 4) {
                    errors.password = "Must be at least 4 characters!";
                }
                if (this.state.mode === "Sign Up") {
                    if (!values.passwordConfirm) {
                        errors.passwordConfirm = "Required";
                    }
                    else if (values.password !== values.passwordConfirm) {
                        errors.passwordConfirm = "Password Field Does Not Match!";
                    }
                }

                return errors;
            }}
        >
            {({ values, handleChange, handleSubmit, errors }) => (
                <div style={{
                    border: "1px grey solid",
                    padding: "15px",
                    borderRadius: "7px"
                }}>
                    <button style={{
                        width: "100%",
                        backgroundColor: "grey",
                        color: "white"
                    }} className="btn btn-lg" onClick={this.switchModeHandler}> Switch to {this.state.mode === "Sign Up" ? "Login" : "Sign Up"} </button>
                    <br />
                    <br />
                    <form onSubmit={handleSubmit}>
                        <input name="email"
                            placeholder="Enter Your Email"
                            className="form-control"
                            value={values.email}
                            onChange={handleChange}
                        />
                        <span style={{ color: "red" }}>{errors.email}</span>
                        <br />
                        <input name="password"
                            placeholder="Password"
                            className="form-control"
                            value={values.password}
                            onChange={handleChange}
                        />
                        <span style={{ color: "red" }}>{errors.password}</span>
                        <br />
                        {this.state.mode === "Sign Up" ? <div>
                            <input
                                name="passwordConfirm"
                                placeholder="Confirm Password"
                                className="form-control"
                                value={values.passwordConfirm}
                                onChange={handleChange}
                            />
                            <span style={{ color: "red" }}>{errors.passwordConfirm}</span>
                            <br />
                        </div> : null}
                        <button type="submit" className="btn btn-success">{this.state.mode === "Sign Up" ? "Sign Up" : "Login"}</button>
                    </form>
                </div>
            )}
        </Formik>
        return (
            <div>
                {form}
            </div>
        )
    }
}
